{
  "rcp": {
    "nome": "Doce de leite Ninho com Nutella",
    "descricao": "Receita para você servir, presentear ou vender e encantar. Confira agora mesmo a delícia!",
    "modoDeFazer": "Misture o leite condensado com o leite em pó em uma panela até diluir. Junte a margarina e o creme de leite e leve ao fogo baixo, mexendo sempre, até desgrudar do fundo (ponto de enrolar). Transfira para um prato untado, cubra com filme plástico em contato com o doce e deixe esfriar. Com as mãos untadas, modele os brigadeiros e envolva em leite em pó. Reserve. Coloque Nutella em saco de confeitar com bico pitanga e leve à geladeira por 10 minutos. Faça um buraquinho no centro do docinho com o bico de confeitar para a Nutella grudar e decore com pitangas do creme.",
    "imagem": "https://static.clubedaanamariabraga.com.br/wp-content/uploads/2019/12/brigadeiro-de-leite-ninho-com-nutella-1024x576.jpg?x41527"
  }, 
  "ing": {
    "ingredientes": [
      {
      "texto": "1 lata de leite condensado"
      },
      {
      "texto": "4 colheres (sopa) de leite Ninho em pó (e mais para enrolar)"
      },
      {
      "texto": "1 colher (sopa) de margarina culinária"
      },
      {
      "texto": "1/2 caixinha de creme de leite (100 g)"
      },
      {
      "texto": "Creme de avelã com cacau para rechear e decorar"
      }
    ]
  }
}

{
  "rcp": {
    "nome": "Docinho de maracujá",
    "descricao": "Delicioso e cremoso!",
    "modoDeFazer": "Em um liquidificador, bata o creme de leite, o leite condensado e o suco concentrado de maracujá. Em uma tigela, despeje a mistura e leve à geladeira por, no mínimo, 4 horas.",
    "imagem": "https://img.itdg.com.br/tdg/images/recipes/000/001/599/55786/55786_original.jpg?mode=crop&width=710&height=400"
  }, 
  "ing": {
    "ingredientes": [
      {
      "texto": "1 lata de leite condensado"
      },
      {
      "texto": "320ml de suco de maracujá"
      },
      {
      "texto": "1 lata de creme de leite sem soro"
      }
    ]
  }
}
